import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import {ToastContainer,toast} from "react-toastify";

import { Container, FormWrapper, Title, Form, Label, Input, Button, ErrorMessage } from '../style';

const SignupForm = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response =  await axios.post('http://localhost:3001/api/signup', { email, password });

            // Navigate to login page after successful signup

            if (response.data.message === 'User already exists') {
                toast.error('Account already exists. Please login.');
            } else {
                toast.success('Signup successful!');
                navigate('/login'); // Navigate to login page after successful signup
            }
        } catch (error) {
            setError('Error signing up');
            toast.error('Error signing up. Please try again.');
        }
    };

    return (
        <Container>
            <FormWrapper>
                <Title>Signup</Title>
                <Form onSubmit={handleSubmit}>
                    {error && <ErrorMessage>{error}</ErrorMessage>}
                    <div>
                        <Label>Name:</Label>
                        <Input type="text"  required></Input>
                    </div>
                    <div>
                        <Label>Date Of Birth</Label>
                        <Input type="date" required></Input>
                    </div>
                    <div>
                        <Label>Email:</Label>
                        <Input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    </div>
                    <div>
                        <Label>Password:</Label>
                        <Input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    </div>
                    <Button type="submit">Signup</Button>
                </Form>
                <br></br>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <h4>If you already have an account</h4>
                    <Link to="/login" style={{ padding: 0, margin: '0 0 0 10px',color:"red" }}>Login</Link>
                 </div>
            <ToastContainer/>  
            </FormWrapper>
        </Container>
    );
};

export default SignupForm;
