import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Correct the import
import SignupForm from './Pages/signup';
import LoginForm from './Pages/Login';
import HomePage from './HomePage';
import AdminPage from './AdminPage';
import ProductsPage from './ProductsPage';

const App = () => {
    const token = localStorage.getItem('token');
    let user = null;

    if (token) {
        try {
            user = jwtDecode(token); // Use the named export
        } catch (error) {
            console.error('Invalid token:', error);
        }
    }

    return (
        <Router>
            <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="/signup" element={<SignupForm />} />
                <Route path="/login" element={<LoginForm />} />
                <Route path="/products" element={user && user.role === 'user' ? <ProductsPage /> : <Navigate to="/login" />} />
                <Route path="/admin" element={user && user.role === 'admin' ? <AdminPage /> : <Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
