    };

    const handleQuantityChange = (productId, newQuantity) => {
        setQuantities(prevQuantities => ({
            ...prevQuantities,
            [productId]: newQuantity
        }));
    };

    const totalPrice = Object.keys(quantities).reduce((total, productId) => {
        const product = cart.find(item => item._id === productId);
        const quantity = quantities[productId];
        return total + (product.price * quantity);
    }, 0);

    const PurchaseItem = async (name) => {
        try {
            const response = await axios.post('http://localhost:3001/api/purchase');

        } catch (error) {
            console.log("Error while purchasing", error);
        }
    };

    return (
        <div className="order-page">
            <h2>Order Page</h2>
            <table className="order-table">
                <thead>
                    <tr>
                        <th>Product Name</th>
                        <th>Quantity</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {cart.map(product => (
                        <tr key={product._id}>
                            <td>{product.title}</td>
                            <td>
                                <input
                                    type="number"
                                    value={quantities[product._id]||0}
                                    onChange={(e) => handleQuantityChange(product._id, parseInt(e.target.value))}
                                    min="1"
                                    className="quantity-input"
                                />
                            </td>
                            <td>
                                <button className="purchase-button" onClick={handleCheckout}>Proceed To Checkout</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="total-price">
                <strong>Total Price: ${totalPrice.toFixed(2)}</strong>
            </div>
        </div>
    );
};

export default OrderPage;
