import React, { useState } from 'react';
import { useCart } from './CartContext';

const OrderPage = () => {
    const { cart, removeFromCart } = useCart();
    const [quantities, setQuantities] = useState({});

    // Update quantity
    const handleQuantityChange = (productId, newQuantity) => {
        setQuantities(prevQuantities => ({
            ...prevQuantities,
            [productId]: newQuantity
        }));
    };

    // Calculate total price
    const totalPrice = Object.keys(quantities).reduce((total, productId) => {
        const product = cart.find(item => item._id === productId);
        const quantity = quantities[productId];
        return total + (product.price * quantity);
    }, 0);

    // Remove product from cart
    const handleRemoveFromCart = (productId) => {
        removeFromCart(productId);
        // Remove quantity from state
        setQuantities(prevQuantities => {
            const { [productId]: _, ...newQuantities } = prevQuantities;
            return newQuantities;
        });
    };

    return (
        <div>
            <h2>Order Page</h2>
            <table>
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Total</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {cart.map(product => (
                        <tr key={product._id}>
                            <td>{product.name}</td>
                            <td>${product.price}</td>
                            <td>
                                <input
                                    type="number"
                                    value={quantities[product._id] || 1}
                                    onChange={(e) => handleQuantityChange(product._id, parseInt(e.target.value))}
                                    min="1"
                                />
                            </td>
                            <td>${(product.price * (quantities[product._id] || 1)).toFixed(2)}</td>
                            <td><button onClick={() => handleRemoveFromCart(product._id)}>Remove</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div>
                <strong>Total Price: ${totalPrice.toFixed(2)}</strong>
            </div>
            <div>
                {/* Add checkout button or continue shopping button */}
            </div>
        </div>
    );
};

export default OrderPage;
